CREATE TABLE "public"."User" ("id" serial NOT NULL, "full_name" text NOT NULL, "mobile_number" text NOT NULL, "email_address" text, "user_role" text NOT NULL, "mobile_verified" boolean NOT NULL DEFAULT false, "email_verified" boolean, "created_at" date NOT NULL DEFAULT now(), "updated_at" date NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("user_role") REFERENCES "public"."user_roles"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"), UNIQUE ("mobile_number"));
